mapper.insert:
INSERT INTO MAGAZINE_IMG SELECT SEQ_MGZ_IMG_NO.NEXTVAL, A.* FROM (
SELECT #{item.imgPath} MGZ_IMG_PATH, #{item.imgName} MGZ_IMG_NAME, #{item.imgLevel} MGZ_IMG_LEVEL, #{item.mgzNo} MGZ_NO FROM DUAL
)A

mapper.select:
SELECT *
FROM MAGAZINE
WHERE DELETE_STATUS = 'N' AND MGZ_NO = #{mgzNo}
