mapper.select:
SELECT COUNT(*)
FROM V_GIFT
WHERE MEM_NO = #{memberNo}
AND PRDT_TYPE = 'G' 
AND ROUND(SYSDATE-CREATE_DATE) <= #{day}

SELECT * FROM V_GIFT
WHERE MEM_NO = #{memberNo}
AND PRDT_TYPE = 'G'
AND ROUND(SYSDATE-CREATE_DATE) <= #{day}

SELECT COUNT(*)
FROM V_GIFT
WHERE MEM_NO = #{memberNo}
AND PRDT_TYPE = 'G' 
AND CREATE_DATE >= #{startDay}
				 AND CREATE_DATE - 1 <= #{endDay}

  SELECT * FROM V_GIFT
	WHERE MEM_NO = #{memberNo}
	AND PRDT_TYPE = 'G'
	AND CREATE_DATE >= #{startDay}
					 AND CREATE_DATE - 1 <= #{endDay}

